package com.javafirst;

public class If3 {
	//// Program to find the sum of natural numbers from 1 to 1000 by using decrement function




	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//sum of 'n' numbers in decremental way
		
		int n = 5;
		int sum = 0;
		
		for (int i = n; i >= 1; i--) {
			sum += i;
			System.out.println("sum = " + sum);
		}
		

	}

}
