

package com.javafirst;

public class JavaBasic {
	
	//Global declaration
	//We have to use the word 'static'
	static char z = 'A';

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Local declaration
		String name = "welcome to Java world";
		int i = 100;
		float f = 10.56f;
		double d = 10000.5677864;
		
		System.out.println("Alphabet starts with : " + z);
		System.out.println(name);
		System.out.println(i);
		System.out.println(f);
		System.out.println(d);
		System.out.println("-----------------");

	}

}
